import React, { useEffect, useState } from 'react';
import { getInventory, placeOrder } from '../api';

const Inventory = () => {
    const [products, setProducts] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const [customerId, setCustomerId] = useState('');

    useEffect(() => {
        async function fetchData() {
            const data = await getInventory();
            setProducts(data);
        }
        fetchData();
    }, []);

    const handlePurchase = async () => {
        if (!selectedProduct || !customerId) {
            alert("Please select a product and enter a customer ID!");
            return;
        }
        await placeOrder(customerId, selectedProduct, quantity);
        alert("Order placed successfully!");
    };

    return (
        <div className="container">
            <h2>Product Inventory</h2>
            <ul>
                {products.map(product => (
                    <li key={product.product_id}>
                        {product.name} - â‚¹{product.price}
                        <button onClick={() => setSelectedProduct(product.product_id)}>Buy</button>
                    </li>
                ))}
            </ul>
            <input type="number" value={quantity} onChange={(e) => setQuantity(e.target.value)} min="1" />
            <input type="text" placeholder="Enter Customer ID" value={customerId} onChange={(e) => setCustomerId(e.target.value)} />
            <button onClick={handlePurchase}>Purchase</button>
        </div>
    );
};

export default Inventory;
