import React, { useEffect, useState } from "react";
import axios from "axios";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [showTable, setShowTable] = useState(false);

  useEffect(() => {
    axios.get("http://localhost:5000/products")
      .then(response => setProducts(response.data))
      .catch(error => console.error("Error fetching products:", error));
  }, []);

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Product Inventory</h2>
      <button 
        onClick={() => setShowTable(!showTable)} 
        style={{
          padding: "10px 15px",
          fontSize: "16px",
          marginBottom: "15px",
          cursor: "pointer",
          backgroundColor: "#007bff",
          color: "#fff",
          border: "none",
          borderRadius: "5px"
        }}
      >
        {showTable ? "Hide Products" : "View Products"}
      </button>

      {showTable && (
        <table border="1" style={{ width: "80%", margin: "auto", borderCollapse: "collapse" }}>
          <thead>
            <tr style={{ backgroundColor: "#007bff", color: "#fff" }}>
              <th style={{ padding: "10px" }}>Product ID</th>
              <th style={{ padding: "10px" }}>Name</th>
              <th style={{ padding: "10px" }}>Price</th>
              <th style={{ padding: "10px" }}>Category</th>
            </tr>
          </thead>
          <tbody>
            {products.map(product => (
              <tr key={product.product_id} style={{ textAlign: "center" }}>
                <td style={{ padding: "10px" }}>{product.product_id}</td>
                <td style={{ padding: "10px" }}>{product.name}</td>
                <td style={{ padding: "10px" }}>â‚¹{product.price}</td>
                <td style={{ padding: "10px" }}>{product.category}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Products;
