import React, { useEffect, useState } from "react";
import axios from "axios";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [showTable, setShowTable] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [newProduct, setNewProduct] = useState({ name: "", price: "", stock: "", category: "" });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    axios.get("http://localhost:5000/products")
      .then(response => setProducts(response.data))
      .catch(error => console.error("Error fetching products:", error));
  };

  const handleAddProduct = () => {
    axios.post("http://localhost:5000/add-product", newProduct)
      .then(response => {
        alert(response.data.message);
        setShowForm(false);
        setNewProduct({ name: "", price: "", stock: "", category: "" });
        fetchProducts(); // Refresh product list
      })
      .catch(error => alert("Error adding product: " + error.response.data.error));
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Product Inventory</h2>
      
      <button 
        onClick={() => setShowTable(!showTable)} 
        style={buttonStyle}>
        {showTable ? "Hide Products" : "View Products"}
      </button>

      <button 
        onClick={() => setShowForm(!showForm)} 
        style={{ ...buttonStyle, backgroundColor: "green", marginLeft: "10px" }}>
        {showForm ? "Cancel" : "Add Product"}
      </button>

      {showForm && (
        <div style={formStyle}>
          <h3>Add New Product</h3>
          <input type="text" placeholder="Name" value={newProduct.name}
            onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })} />
          <input type="number" placeholder="Price" value={newProduct.price}
            onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })} />
          <input type="number" placeholder="Stock" value={newProduct.stock}
            onChange={(e) => setNewProduct({ ...newProduct, stock: e.target.value })} />
          <input type="text" placeholder="Category" value={newProduct.category}
            onChange={(e) => setNewProduct({ ...newProduct, category: e.target.value })} />
          <button onClick={handleAddProduct} style={{ ...buttonStyle, backgroundColor: "#28a745" }}>
            Submit
          </button>
        </div>
      )}

      {showTable && (
        <table border="1" style={tableStyle}>
          <thead>
            <tr style={{ backgroundColor: "#007bff", color: "#fff" }}>
              <th>Product ID</th>
              <th>Name</th>
              <th>Price</th>
              <th>Stock</th>
              <th>Category</th>
            </tr>
          </thead>
          <tbody>
            {products.map(product => (
              <tr key={product.product_id} style={{ textAlign: "center" }}>
                <td>{product.product_id}</td>
                <td>{product.name}</td>
                <td>â‚¹{product.price}</td>
                <td>{product.stock}</td>
                <td>{product.category}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

const buttonStyle = {
  padding: "10px 15px",
  fontSize: "16px",
  marginBottom: "15px",
  cursor: "pointer",
  backgroundColor: "#007bff",
  color: "#fff",
  border: "none",
  borderRadius: "5px"
};

const formStyle = {
  margin: "20px auto",
  padding: "20px",
  width: "300px",
  display: "flex",
  flexDirection: "column",
  gap: "10px",
  border: "1px solid #ccc",
  borderRadius: "5px",
  backgroundColor: "#f9f9f9"
};

const tableStyle = {
  width: "80%",
  margin: "auto",
  borderCollapse: "collapse"
};

export default Products;
